import React, { useState } from 'react';
import { FaSearch, FaMicrophone } from 'react-icons/fa';
import { Document, Page } from 'react-pdf';


import './App.css';

function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchBarFocused, setIsSearchBarFocused] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [showPDFViewer, setShowPDFViewer] = useState(false);

  const handleSearchBarClick = () => {
    setIsSearchBarFocused(true);
  };

  const handleSearchQueryChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handlePDFLinkClick = () => {
    setShowPDFViewer(true);
  };

  const handleBackToLinksClick = () => {
    setShowPDFViewer(false);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Perform search or URL navigation logic here
    console.log('Search query:', searchQuery);
  };

  return (
    <div className="app">
      <header className="header">
        <img className="logo"src="https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png"
 alt="Google" />
      </header>
      <form className="search-form" onSubmit={handleSearchSubmit}>
      <div className={`search-bar ${isSearchBarFocused ? 'focused' : ''}`}>
        <FaSearch className="search-icon" />
        <input
          type="text"
          className="search-input"
          value={searchQuery}
          onChange={handleSearchQueryChange}
          onClick={handleSearchBarClick}
          placeholder="Tell me about Jonathan Pan"
        />
        <FaMicrophone className="mic-icon" />
      </div>
      </form>
      {showPDFViewer ? (
        <div>
          <Document file="/path/to/your/file.pdf" onLoadSuccess={({ numPages }) => setNumPages(numPages)}>
            <Page pageNumber={pageNumber} />
          </Document>
          <p>Page {pageNumber} of {numPages}</p>
          <button onClick={handleBackToLinksClick}>Back to Links</button>
        </div>
      ) : (
        <footer className="footer">
          <button onClick={handlePDFLinkClick}>
            <FaGlobe className="footer-icon" />
            All
          </button>
          {/* Add more quick link buttons */}
        </footer>
      )}
    </div>
  );
}

export default App;
