import React, { useState } from 'react';
import './styles.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);

  const handleSendMessage = (messageText) => {
    const newMessage = {
      text: messageText,
      sender: 'user',
    };

    setMessages([...messages, newMessage]);

    // Simulate a response from the server after a delay
    setTimeout(() => {
      const responseMessage = {
        text: 'This is a sample response from ChatGPT.',
        sender: 'chatbot',
      };

      setMessages([...messages, responseMessage]);
    }, 500);
  };

  const handleSelectChat = (chat) => {
    setSelectedChat(chat);
  };

  return (
    <div className="app">
      <div className="sidebar">
        <div className="sidebar-header">ChatGPT</div>
        <div className="sidebar-chats">
          <div
            className={`sidebar-chat ${selectedChat === null ? 'selected' : ''}`}
            onClick={() => handleSelectChat(null)}
          >
            + New Chat
          </div>
          <div className="sidebar-chat">Previous Chat 1</div>
          <div className="sidebar-chat">Previous Chat 2</div>
        </div>
      </div>

      <div className="chat-window">
        <header className="header">ChatGPT Portfolio</header>

        <div className="message-container">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message ${message.sender === 'user' ? 'user' : 'chatbot'}`}
            >
              {message.text}
            </div>
          ))}
        </div>

        <div className="input-container">
          <input
            type="text"
            className="message-input"
            placeholder="Type your message..."
            onKeyDown={(event) => {
              if (event.key === 'Enter' && event.target.value.trim() !== '') {
                handleSendMessage(event.target.value.trim());
                event.target.value = '';
              }
            }}
          />
          <button className="send-button">
            <svg
              className="send-icon"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <line x1="5" y1="12" x2="19" y2="12" />
              <polyline points="12 5 19 12 12 19" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
