import React, { useState } from 'react';
import { FaSearch, FaMicrophone } from 'react-icons/fa';
import { Document, Page } from 'react-pdf';

import './App.css';

function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchBarFocused, setIsSearchBarFocused] = useState(false);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [showPDF, setShowPDF] = useState(false);
  const [pdfData, setPdfData] = useState(null);

  const handleSearchBarClick = () => {
    setIsSearchBarFocused(true);
  };

  const handleSearchQueryChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    console.log('Search query:', searchQuery);
  };

  const handleResumeClick = async (e) => {
    e.preventDefault();
    const response = await fetch('JonathanPan2023Resume.pdf');
    const blob = await response.blob();
    setPdfData(blob);
    setShowPDF(true);
  };

  return (
    <div className="app">
      <header className="header">
        <img
          className="logo"
          src="https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png"
          alt="Google"
        />
      </header>
      <form className="search-form" onSubmit={handleSearchSubmit}>
        <div className={`search-bar ${isSearchBarFocused ? 'focused' : ''}`}>
          <FaSearch className="search-icon" />
          <input
            type="text"
            className="search-input"
            value={searchQuery}
            onChange={handleSearchQueryChange}
            onClick={handleSearchBarClick}
            placeholder="Tell me about Jonathan Pan"
          />
          <FaMicrophone className="mic-icon" />
        </div>
      </form>
      {showPDF && pdfData ? (
        <div className="pdf-container">
          <Document file={pdfData} onLoadSuccess={({ numPages }) => setNumPages(numPages)}>
            <Page pageNumber={pageNumber} />
          </Document>
          <p>
            Page {pageNumber} of {numPages}
          </p>
        </div>
      ) : (
        <footer className="footer">
          <a href="#">About</a>
          <a href="#" onClick={handleResumeClick}>
            Resume
          </a>
          <a href="https://www.linkedin.com/in/jonathan-pan/">LinkedIn</a>
        </footer>
      )}
    </div>
  );
}

export default App;
